    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
       
        String usuario = user.getText();
        String contraseña = new String(pass.getPassword());
        CreateUser obj = new CreateUser();
        
        if(si.isSelected()){
            obj.crearUser(usuario, contraseña, 1);  //Si es un usuario administrador es 1
        }else{
            obj.crearUser(usuario, contraseña, 0);  //Si es un usuario comun es 0
        }
        
        Archivo_Conexiones ac = new Archivo_Conexiones();
        ac.escribirArchivo(usuario, contraseña, "");
        
        MenuUsuarios acc = new MenuUsuarios();
        acc.setVisible(true);
        this.dispose();
        
        
    }//GEN-LAST:event_jButton1MouseClicked

